import React, {Component} from "react";

class MovieCard extends Component{
    constructor(props){
        super(props)
        this.movie = props.movie
        // this.movie = {
        //         mal_id: 5114,
        //         rank: 1,
        //         title: "Fullmetal Alchemist: Brotherhood",
        //         url: "https://myanimelist.net/anime/5114/Fullmetal_Alchemist__Brotherhood",
        //         image_url: "https://cdn.myanimelist.net/images/anime/1223/96541.jpg?s=faffcb677a5eacd17bf761edd78bfb3f",
        //         type: "TV",
        //         episodes: 64,
        //         start_date: "Apr 2009",
        //         end_date: "Jul 2010",
        //         members: 2649743,
        //         score: 9.16
        // }
    }
    
    render(){
        return(
            <div className="col-12 my-3">
              <div className="card h-100">
                <img
                  src={this.movie.image_url}
                  class="card-img-top"
                  alt="skilvul"
                />
                <div class="card-body">
                  <h5 class="card-title">{this.movie.title}</h5>
                  <h6 class="card-subtitle mb-2">
                    <span className={`badge ${this.movie.type === "TV" ? 'bg-danger' : 'bg-success'}`}>{this.movie.type}</span>
                  </h6>
                  <p class="card-text">
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </p>
                </div>
                <div class="card-body">
                  <a
                    href={this.movie.url}
                    class="btn btn-primary w-100 text-uppercase"
                  >
                    readmore
                  </a>
                </div>
              </div>
            </div>
        )
    }
}

export default MovieCard
